 * Circulargauge Control
 */
import { NgModule, ModuleWithProviders, Type } from '@angular/core';

import { CircularGaugeAllModule } from '@syncfusion/ej2-ng-circulargauge';
import { GridModule } from '@syncfusion/ej2-ng-grids';





















