#control_wrapper {
    max-width: 246px;
    margin: 30px auto;
    padding-top: 50px;
}

.control-section {
    margin: 25px auto;
}

#control_wrapper #container {
    min-height: 300px;
}

.e-bigger #control_wrapper #container {
    min-height: 380px;
}

span.e-icons.highlight {
    margin-top: -13px;
    display: block;
    margin-left: 4px;
}

span.e-icons.highlight,
span.e-icons.highlight:before {
    color: rgb(0, 0, 255);
}

span.e-icons.highlight:before {
    content: "\e865";
    vertical-align: middle;
    margin-right: 3px;
    font-size: 4px;
    position: relative;
    top: -1px;
    font-weight: normal;
}

.e-selected span.e-icons.highlight:before {
    color: #fff;
}

span.e-icons.highlight {
    margin-top: -11px;
    display: block;
    margin-left: 4px;
}

.e-calendar .e-content span.special,
.e-calendar .e-content span.daycell {
